import { Recepie } from './recepie.model';
import { Subject } from 'rxjs-compat/Subject';
import { Ingredient } from '../shared/ingredient.model';
export class RecepieService {
  constructor() {
    this.recepiesChanged = new Subject();
    this.recepies = [new Recepie('Cheesecake', 'Sweet vanila cake!', 'https://www.thecheesecakefactory.com/assets/images/Menu-Import/CCF_FreshStrawberryCheesecake.jpg', [new Ingredient('Cheese', 1), new Ingredient('Sugar', 1)]), new Recepie('Chockolate Cake', 'Cake made of chockolate', 'http://ichef.bbci.co.uk/food/ic/food_16x9_448/recipes/easy_chocolate_cake_31070_16x9.jpg', [new Ingredient('Chocolate', 3), new Ingredient('Cherry', 1)])];
  }

  setRecepies(recepies) {
    this.recepies = recepies;
    this.recepiesChanged.next(this.recepies.slice());
  }

  getRecepies() {
    return this.recepies.slice();
  }

  getRecepieId(index) {
    return this.recepies.slice()[index];
  }

  addRecepie(recepie) {
    this.recepies.push(recepie);
    this.recepiesChanged.next(this.recepies.slice());
  }

  updateRecepie(index, newRecepie) {
    this.recepies[index] = newRecepie;
    this.recepiesChanged.next(this.recepies.slice());
  }

  deleteRecepie(index) {
    this.recepies.splice(index, 1);
    this.recepiesChanged.next(this.recepies.slice());
  }

}
